  const REWARD_TOKEN = "0x3333b97138D4b086720b5aE8A7844b1345a33333";  // SHADOW
  const REWARD_TOKEN_2 = "0x5050bc082FF4A74Fb6B0B04385dEfdDB114b2424";  // xSHADOW
  
  async function main() {
    const App = await init_ethers();
  
    _print(`Initialized ${App.YOUR_ADDRESS}\n`);
    _print("Loading shadow gauges...\n");
  
    const FLOW_VOTER_ADDR = "0x3aF1dD7A2755201F8e2D6dCDA1a61d9f54838f4f"
    const FLOW_VOTER_CONTRACT = new ethcall.Contract(FLOW_VOTER_ADDR, FLOW_VOTER_ABI);
  
    let v2Gauges = [], clGauges = [], activeV2Gauges = [], activeClGauges = [];
  
    const [gauges_] = await App.ethcallProvider.all([FLOW_VOTER_CONTRACT.getAllGauges()]);
    const gauges = gauges_.flat();

    const isClGaugeCalls = gauges.map(a => FLOW_VOTER_CONTRACT.isClGauge(a));
    const isClGauges = await App.ethcallProvider.all(isClGaugeCalls);

    for(let i = 0; i < gauges.length; i++){
      if(isClGauges[i]){
        clGauges.push(gauges[i]);
      }else{
        v2Gauges.push(gauges[i]);
      }
    }

    const v2GaugeContracts = v2Gauges.map(v2g => new ethcall.Contract(v2g, FLOW_GAUGE_ABI));
    const rewardDataCalls = v2GaugeContracts.map(v2gc => v2gc.rewardData(REWARD_TOKEN_2));
    const v2RewardsData = await App.ethcallProvider.all(rewardDataCalls);
    for(let i = 0; i < v2GaugeContracts.length; i++){
      if(v2RewardsData[i].rewardRate / 1 > 0 && (Date.now() / 1000 < (v2RewardsData[i].periodFinish))){
        activeV2Gauges.push(v2GaugeContracts[i].address);
      }
    }

    const clGaugeContracts = clGauges.map(clg => new ethcall.Contract(clg, CL_GAUGE_ABI));
    const clRewardRateCalls = clGaugeContracts.map(clgc => clgc.rewardRate(REWARD_TOKEN_2));
    const clRewardRates = await App.ethcallProvider.all(clRewardRateCalls);
    for(let i = 0; i < clGaugeContracts.length; i++){
      if(clRewardRates[i] > 0){
        activeClGauges.push(clGaugeContracts[i].address);
      }
    }

    const v2_gages_to_lowercase = activeV2Gauges.map(v2 => v2.toLowerCase());
    const cl_gages_to_lowercase = activeClGauges.map(cl => cl.toLowerCase());

    // starting second part of the script

    // http request to vfat-api
    const response = await $.ajax({
      url: 'https://api.vfat.io/v1/farms',
      type: 'GET',
    });

    const response2 = await $.ajax({
      url: 'https://api.vfat.io/v1/tokens?chainId=146&pageSize=999',
      type: 'GET',
    });

    const vfat_io_tokens = response2.tokens.map(t => t.address.toLowerCase());

    const vfat_gauges = response.filter(d => d.chainId == 146).map(ob => ob.address.toLowerCase());

    let missing_v2_gauges = [];
    let missing_cl_gauges = [];
    let _v2_list = [];
    let _cl_list = [];

    for(const gauge of v2_gages_to_lowercase){
      const is_in_list = vfat_gauges.includes(gauge);
      if(!is_in_list){
          missing_v2_gauges.push(gauge);
      }
  }

  _print_bold("V2 POOLS");
  _print_bold("Missing v2 pools count " + missing_v2_gauges.length);
  _print(``);

  for(const v2_address of missing_v2_gauges){
    const v2_data = await getV2Data(App, v2_address, vfat_io_tokens);
    _v2_list.push(v2_data);
}

_print(``);
  const v2_list = _v2_list.flat();
  const v2_list_no_dublicates = remove_dublicates(v2_list, ob => ob.symbol);
  for(const v2 of v2_list_no_dublicates){
      _print(v2)
  }

  _print(``);

  for(const gauge of cl_gages_to_lowercase){
      const is_in_list = vfat_gauges.includes(gauge);
      if(!is_in_list){
          missing_cl_gauges.push(gauge);
      }
  }

  _print_bold(`CL POOLS`);
  _print(``);

  for(const cl_gauge of missing_cl_gauges){
    const cl_data = await getClData(App, cl_gauge, vfat_io_tokens);
    _cl_list.push(cl_data);
  }

  const cl_list = _cl_list.flat();
  const cl_list_no_dublicates = remove_dublicates(cl_list, ob => ob.symbol);
  for(const cl of cl_list_no_dublicates){
      _print(cl);
  }
  
  hideLoading();
}

function remove_dublicates(arr, key){
  return [
      ...new Map(
          arr.map(x => [key(x), x])
      ).values()
  ]
}

